import { toBeEmpty } from "@testing-library/jest-dom";
import axios from "axios";
import React from "react";
import { useState, useEffect } from "react";
import { toast } from "react-toastify";
import "react-toastify/dist/ReactToastify.css";
import "./booking.css";
import { useNavigate } from "react-router-dom";
import Footer from "./Footer";
toast.configure();
const Booking = () => {
  // Start
  const [washers, setWashers] = useState([]);

  useEffect(() => {
    fetch("http://localhost:8081/admin/allpacks").then((result) => {
      result.json().then((resp) => {
        setWashers(resp);
        console.log(resp);
      });
    });
  }, []);

  // End
  let navigate = useNavigate();

  const [order, setOrder] = useState({
    carModel: "",
    carName: "",
    contactno: "",
    date: "",
    orderId: "",
    username: "",
    washpackId: "",
    address: "",
  });

  const changeHandler = (e) => {
    setOrder({ ...order, [e.target.name]: e.target.value });
  };

  const submitHandler = () => {
    axios
      .post("http://localhost:8083/order/addorder", order)
      .then((res) => {
        if (res.status === 200) alert("Booked Successfully");
        else Promise.reject();
      })
      .catch((err) => alert("Something went wrong"));
  };

  return (
    <>
      <div className="formTop">
        <div className="formTitle">
          <p className="c1">
            <h1>BOOK YOUR CAR WASH </h1>
          </p>
        </div>
      </div>
      <div>
        <div className="formDetails">
          <form className="actualForm" onSubmit={submitHandler}>
            <div style={{ marginBottom: "30px" }}>
              <p className="formName">FILL BELOW DETAILS</p>
            </div>
            <div className="formDateTime">
              <input type="date" name="date" onChange={changeHandler} />
            </div>

            {washers.map((emp, ind) => (
              <div style={{ marginBottom: "20px" }} key={emp.packname}>
                <option>{emp.packname}</option>
                <input type="radio" onChange={changeHandler} /> {emp.cost}
              </div>
            ))}

            <div style={{ marginBottom: "30px" }}>
              <p className="formName">CONTACT DETAILS</p>
            </div>
            <div>
              <input
                className="contactDetails"
                type="text"
                name="username"
                onChange={changeHandler}
                placeholder="Enter Your Full Name"
              />
              <br />
              <input
                className="contactDetails"
                name="carName"
                type="text"
                placeholder="Enter Your Car Name"
                onChange={changeHandler}
              />
              <br />

              <input
                className="contactDetails"
                type="text"
                name="carModel"
                placeholder="Enter Your Car Model"
                onChange={changeHandler}
              />
              <br />

              <input
                type="text"
                className="contactDetails"
                name="contactno"
                onChange={changeHandler}
                placeholder="Enter Phone"
              />
              <br />

              <textarea
                cols="30"
                className="contactDetailsTextArea"
                rows="10"
                type="text"
                name="address"
                onChange={changeHandler}
                placeholder="Your Address"
              />
            </div>
            <div className="Submit">
              <button
                type="submit"
                className="btn btn-success"
                onClick={() => {
                  navigate("/Payment");
                }}

                // onSubmit={submitHandler}
              >
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>
      <div>
        <Footer />
      </div>
    </>
  );
};
export default Booking;
